import * as api from '../api'
import { ADDUSERPROFILE, UPDATEUSERPROFILE } from './actionTypes.js'



export const getUserProfile = id => async dispatch => {
  try {
    const { data } = await api.fetchPreferences(id)
    dispatch({ type: 'GET', payload: data })
  } catch (error) {
    console.log(error)
  }
}
export const addPrefences = preferences => async dispatch => {
  try {
    const { data } = await api.addPrefences(preferences)
    dispatch({ type: 'ADD', payload: data })
  } catch (error) {
    console.log(error)
  }
}
