import React from 'react'
import { useSelector } from 'react-redux'
import { Grid, CircularProgress, Typography } from '@material-ui/core'

import Post from './post/Post.js'
import useStyles from './styles.js'
import Book from './userBook.js'

const Posts = () => {
  const results = useSelector(state => state.books)
  const classes = useStyles()
  const user = JSON.parse(localStorage.getItem('profile')).result
  const userProfile = useSelector(state => state.profile)
  const likes = userProfile.likes
  console.log('====', results)
  let recommendBooks
  if (results.length > 1) recommendBooks = results[1]

  const books = results[0].filter(book => book.ownerId !== user._id)
  const currentUserBook = results.filter(book => book.ownerId === user._id)

  const configureLike = id => {
    for (let i = 0; i < likes.length; i++) {
      if (likes[i].bookId === id) return likes[i]
    }
    return false
  }

  return !results.length ? (
    <CircularProgress />
  ) : (
    <Grid
      className={classes.container}
      container
      alignItems='stretch'
      spacing={3}
    >
      {books.length > 0 &&
        books.map(post => (
          <Grid item key={post._id} xs={12} sm={6}>
            <Post post={post} like={configureLike(post._id)} />
          </Grid>
        ))}
      <Typography
        variant='h4'
        component='h4'
        style={{ display: 'block', alignItems: 'center' }}
      >
        Your Books
      </Typography>
      {currentUserBook.length > 0 &&
        currentUserBook.map(book => <Book book={book} />)}
    </Grid>
  )
}
export default Posts
