import { CREATE, FETCH_ALL } from '../actions/actionTypes.js'

const preferenceReducer = (preferences = [], action) => {
  switch (action.type) {
    case CREATE:
      return preferences
    case FETCH_ALL:
      localStorage.clear()
      return { ...state, preferences: action?.data }

    default:
      return state
  }
}
export default preferenceReducer
