import { FETCH_ALL, CREATE } from '../actions/actionTypes.js'

const getPreferences = (state = { preferences: null }, action) => {
  switch (action.type) {
    case CREATE:
      return { ...state, preferences: action?.data }
    case FETCH_ALL:
      return state

    default:
      return state
  }
}
export default getPreferences
