import React, { useState } from 'react'
import { Button, Typography, Paper, TextField } from '@material-ui/core'

import makeStyles from './styles.js'

const Preferences = () => {
  const [preferences, setpreferences] = useState([])
  const [postData, setPostData] = useState()
  const classes = makeStyles()

  const handleSubmit = () =>{

  }

  const addKeyword = e => {
    e.preventDefault()
    let key = postData.keywords
    if (keyword.length === 0) return
    if (key) key.push(keyword)
    else key = keyword
    setPostData({ ...postData, keywords: key })
    setkeyword('')

  }
  return (
    <Paper className={classes.paper}>
      <form
        autoComplete='off'
        noValidate
        className={`${classes.root} ${classes.form}`}
        onSubmit={handleSubmit}
      >
        <Typography variant='h5'>Add Your Preferences</Typography>
        <div
          style={{
            display: 'flex'
          }}
        >
          <TextField
            name='keyword'
            variant='outlined'
            label='Keyword'
            fullWidth
            onChange={e => setkeyword(e.target.value)}
          />
          <AddCircleRoundedIcon
            sx={{ width: '3rem', height: '3rem', marginTop: '0.2rem' }}
            className={classes.addIcon}
            onClick={addKeyword}
          />
        </div>
        {postData.keywords.length > 0 &&
          postData.keywords.map(post => (
            <h5 className={classes.keywords}>{post}</h5>
          ))}

        <Button
          className={classes.buttonSubmit}
          variant='contained'
          color='primary'
          size='large'
          type='submit'
          fullWidth
        >
          SUBMIT
        </Button>
        <Button
          className={classes.buttonSubmit}
          variant='contained'
          color='secondary'
          size='small'
          onClick={e => clear(e)}
          fullWidth
        >
          CLEAR
        </Button>

      </form>
    </Paper>
  )
}

export default Preferences
