import React, { useState } from 'react'
import { Button, Typography, Paper, TextField } from '@material-ui/core'
import AddCircleRoundedIcon from '@mui/icons-material/AddCircleRounded'
import { useDispatch, useSelector } from 'react-redux'

import makeStyles from './styles.js'
import { addPrefences, fetchPreferences } from '../../actions/users.js'

const Preferences = () => {
  const user = JSON.parse(localStorage.getItem('profile')).result
  const [postData, setPostData] = useState({ userId: user._id, keywords: '' })

  const classes = makeStyles()
  const dispatch = useDispatch()
  const preferences = useSelector(state => state.preferences)

  useEffect(() => {
    dispatch(fetchPreferences(user._id))
  }, [dispatch])

  console.log('preferences', preferences)

  const handleSubmit = e => {
    e.preventDefault()
    dispatch(addPrefences(postData))
  }

  return (
    <Paper className={classes.paper}>
      <form
        autoComplete='off'
        noValidate
        className={`${classes.root} ${classes.form}`}
        onSubmit={handleSubmit}
      >
        <Typography variant='h5'>Add Your Preferences</Typography>

        <TextField
          name='keyword'
          variant='outlined'
          label='Preferences'
          value={postData.keywords}
          fullWidth
          onChange={e => setPostData({ ...postData, keywords: e.target.value })}
        />

        <Button
          className={classes.buttonSubmit}
          variant='contained'
          color='primary'
          size='large'
          type='submit'
          fullWidth
        >
          <AddCircleRoundedIcon
            className={classes.addIcon}
            sx={{ width: '3rem', height: '2rem', marginTop: '0.3rem' }}
          />
        </Button>
      </form>
    </Paper>
  )
}

export default Preferences
