import * as api from '../api'
import { FETCH_ALL, CREATE } from './actionTypes.js'

export const getPreferences = (id) => async dispatch => {
  try {
    const { data } = await api.fetchPreferences(id)
    console.log("data from data base :preference ",data)

    dispatch({ type: FETCH_ALL, payload: data })
  } catch (error) {
    console.log(error)
  }
}
export const addPrefences = (preferences) => async dispatch => {
  try {
    const { data } = await api.addPrefences(preferences)
    dispatch({ type: CREATE, payload: data })
  } catch (error) {
    console.log(error)
  }
}
