const Input = (name, half, type) => {
  return (
    <Grid item xs={12} sm={half ? 6 : 12}>
      <TextField
        name={name}
        onChange={handleChange}
        varient='outlined'
        required
        fullWidth
        label={label}
        autoFocus={autoFocus}
        type={type}
        InputProps={
          name === 'password' && {
            endAdorment: (
              <InputAdorment position='end'>
                <IconButton onClick={handleShowPassword}>
                  {type === 'passwprd' ? <Visibility /> : <VisibilityOff />}
                </IconButton>
              </InputAdorment>
            )
          }
        }
      />
    </Grid>
  )
}

export default Input
