const getPreferences = (state = { preferences: [] }, action) => {
  switch (action.type) {
    case 'ADD':
      console.log(action.payload)

      return { ...state, preferences: action?.payload }
    case 'GET':
      return action.payload

    default:
      return state
  }
}
export default getPreferences
