import { CREATE, FETCH_ALL } from '../actions/actionTypes.js'

const preferenceReducer = (state = {preferences:[]}, action) => {
  switch (action.type) {
    case CREATE:
      return {...state, preferences: action.payload}

    case FETCH_ALL:
      return state

    default:
      return state
  }
}
export default preferenceReducer
