import {FETCH_ALL, CREATE} from '../actions/actionTypes'


export default (preferences = [], action) => {
    switch (action.type) {
      case FETCH_ALL:
        return action.payload
      case CREATE:
        return action.payload
      default:
        return preferences
    }
  }
